mapper.select:
 select * from product where 
 
		 
			product_state=#{product_state}
		
 	
 		product_state='I' or product_state='D'
		
 
 order by product_code desc limit #{cri.pageStart},#{cri.perPageNum}

 select * from product where 
 
		 
			product_state=#{product_state} and
			product_maincategory=#{product_maincategory} and
			product_subcategory=#{product_subcategory}
		
 	
 		product_state='I' or product_state='D'
		
 
 order by product_code desc limit #{cri.pageStart},#{cri.perPageNum}

	select * from product where 
  
 		 
 			product_state=#{product_state} and
 			product_maincategory=#{product_maincategory}
 		
  	
  		product_state='I' or product_state='D'
 		
  
  order by product_code desc limit #{cri.pageStart},#{cri.perPageNum}

 select count(*) from product where 
 
		 
			product_state=#{product_state}
		
 	
 		product_state='I' or product_state='D'

select count(*) from product where
product_state=#{product_state} and product_maincategory=#{product_maincategory} and product_subcategory=#{product_subcategory}
product_state='I' or product_state='D'

select count(*) from product where
product_state=#{product_state} and product_maincategory=#{product_maincategory}
product_state='I' or product_state='D'

mapper.update:
update product set
product_title =#{product.product_title}, product_maincategory=#{product.product_maincategory}, product_subcategory=#{product.product_subcategory}, product_origin=#{product.product_origin}, product_made=#{product.product_made}, product_price=#{product.product_price}, product_stock=#{product.product_stock}, product_contents=#{product.product_contents}, file=#{product.file}, contentsfile=#{product.contentsfile}, product_views=#{product.product_views}, product_state=#{product.product_state}
where product_code=#{product.product_code}
